export const TodoList=({todoList,setTodoList})=>{

    function onClickChecked(id){
        const checkedArr = todoList.map((el)=>{
            if(el.id === id) {
                el.isChecked = true
            }
            return el
        })
        setTodoList(checkedArr)
        }

    // }
