export const TodoList=({todoList,setTodoList})=>{
    function onClickDelete(id){
        const updatedArr = todoList.map((el)=>{
            if (el.id === id){
                el.isDeleted = true
            }
            return el

        }) 
        setTodoList(updatedArr)
    }
    return(
        <>
        <ol>
            {todoList.map(({content},index,isDeleted)=>{
                return(
                    <li>
                    <div style = {{ textDecoration: isDeleted ? "line-through" : ""}} 
                    key={`${content}+${index}`}>
                    {content}
                    {!isDeleted}  <button onClick={onClickDelete}>delete</button>

                    </div>
                    </li>
                )
            })}
        </ol>
        </>
    )
}
