export const TodoList=({todoList})=>{
    function onClickDelete(id){
        const updatedArr = todoList.map((el)=>{
            if (el.id === id){
                el.isDeleted = true
            }
            return el

        }) 
        setTodoList(updatedArr)
    }
    return(
        <>
        <ol>
            {todoList.map(({content},index)=>{
                return(
                    <div
                    key={`${content}+${index}`}>
                    {index+1}.{content}
                    <button onClick={onClickDelete}>delete</button>
                    </div>
                )
            })}
        </ol>
        </>
    )
}
