export const TodoList=({todoList,setTodoList})=>{
    function onClickDelete(id){
        const updatedArr = todoList.map((el)=>{
            if (el.id === id){
                el.isDeleted = true
            }
            return el

        }) 
        setTodoList(updatedArr)
    }
    return(
        <>
        <ol>
            {todoList.map(({content,isDeleted,id},index)=>{
                return(
                    <li>
                    <div  
                    key={`${content}+${index}`}>
                    {content}
                    {/* {!isDeleted && <button onClick={() => onClickDelete(id)}>Delete</button>} */}

                    </div>
                    </li>
                )
            })}
        </ol>
        </>
    )
}
